import React, { useState } from "react";
import Button from "../content/button";

export default function Content() {
  const quiz = [
    {
      questionText: "뚜뚜.jpg",
      answerOptions: [
        { answerText: "삐삐", isCorrect: false },
        { answerText: "뚜뚜", isCorrect: true },
        { answerText: "크롱", isCorrect: false },
        { answerText: "뽀뽀", isCorrect: false },
      ],
    },
    {
      questionText: "로디.jpg",
      answerOptions: [
        { answerText: "포비", isCorrect: false },
        { answerText: "루피", isCorrect: false },
        { answerText: "패티", isCorrect: false },
        { answerText: "로디", isCorrect: true },
      ],
    },
    {
      questionText: "루피.jpg",
      answerOptions: [
        { answerText: "루피", isCorrect: true },
        { answerText: "포비", isCorrect: false },
        { answerText: "크롱", isCorrect: false },
        { answerText: "에디", isCorrect: false },
      ],
    },
    {
      questionText: "뽀로로.jpg",
      answerOptions: [
        { answerText: "통통이", isCorrect: false },
        { answerText: "크롱", isCorrect: false },
        { answerText: "에디", isCorrect: false },
        { answerText: "뽀로로", isCorrect: true },
      ],
    },
    {
      questionText: "에디.jpg",
      answerOptions: [
        { answerText: "포비", isCorrect: false },
        { answerText: "해리", isCorrect: false },
        { answerText: "에디", isCorrect: true },
        { answerText: "삐삐", isCorrect: false },
      ],
    },
    {
      questionText: "크롱.jpg",
      answerOptions: [
        { answerText: "크롱", isCorrect: true },
        { answerText: "루피", isCorrect: false },
        { answerText: "뽀로로", isCorrect: false },
        { answerText: "에디", isCorrect: false },
      ],
    },
    {
      questionText: "통통이.jpg",
      answerOptions: [
        { answerText: "포비", isCorrect: false },
        { answerText: "뽀뽀", isCorrect: false },
        { answerText: "통통이", isCorrect: true },
        { answerText: "뚜뚜", isCorrect: false },
      ],
    },
    {
      questionText: "패티.jpg",
      answerOptions: [
        { answerText: "포비", isCorrect: false },
        { answerText: "크롱", isCorrect: false },
        { answerText: "에디", isCorrect: false },
        { answerText: "패티", isCorrect: true },
      ],
    },
    {
      questionText: "해리.jpg",
      answerOptions: [
        { answerText: "해리", isCorrect: true },
        { answerText: "패티", isCorrect: false },
        { answerText: "크롱", isCorrect: false },
        { answerText: "에디", isCorrect: false },
      ],
    },
    {
      questionText: "삐삐.jpg",
      answerOptions: [
        { answerText: "뽀뽀", isCorrect: false },
        { answerText: "크롱", isCorrect: false },
        { answerText: "루피", isCorrect: false },
        { answerText: "삐삐", isCorrect: true },
      ],
    },
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quiz.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };
  return (
    <div className="app">
      {showScore ? (
        <div className="score-section">
          You scored {score} out of {quiz.length}
        </div>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{quiz.length}
            </div>
            <div className="question-text">
              <img
                src={`./img/${quiz[currentQuestion].questionText}`}
                alt={quiz[currentQuestion].questionText}
              />
            </div>
          </div>
          <div className="answer-section">
            {quiz[currentQuestion].answerOptions.map((answerOption) => (
              <Button
                onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}
              >
                {answerOption.answerText}
              </Button>
            ))}
          </div>
        </>
      )}
    </div>
  );
}
