import React, { useState } from "react";
import styled from "styled-components";
import img01 from "../img/뚜뚜.jpg";
import img02 from "../img/로디.jpg";
import img03 from "../img/루피.jpg";
import img04 from "../img/뽀로로.jpg";
import img05 from "../img/뽀뽀,삐삐.jpg";
import img06 from "../img/에디.jpg";
import img07 from "../img/크롱.jpg";
import img08 from "../img/통통이.jpg";
import img09 from "../img/패티.jpg";
import img10 from "../img/포비.jpg";
import img11 from "../img/해리.jpg";
// function handleButtonClick() {
//   console.log("냥");
// }

export default function button() {
  // 여기서부터 추가
  const quiz = [
    {
      quizImg: { img02 },
      quizOptions: [
        { answer: "크롱", isCorrect: false },
        { answer: "루피", isCorrect: false },
        { answer: "로디", isCorrect: true },
        { answer: "해리", isCorrect: false },
        { answer: "뽀로로", isCorrect: false },
      ],
    },
    {
      quizImg: { img03 },
      quizOptions: [
        { answer: "크롱", isCorrect: false },
        { answer: "루피", isCorrect: true },
        { answer: "로디", isCorrect: false },
        { answer: "해리", isCorrect: false },
        { answer: "뽀로로", isCorrect: false },
      ],
    },
    {
      quizImg: { img04 },
      quizOptions: [
        { answer: "크롱", isCorrect: false },
        { answer: "루피", isCorrect: false },
        { answer: "로디", isCorrect: false },
        { answer: "해리", isCorrect: false },
        { answer: "뽀로로", isCorrect: true },
      ],
    },
    {
      quizImg: { img05 },
      quizOptions: [
        { answer: "크롱", isCorrect: false },
        { answer: "루피", isCorrect: false },
        { answer: "로디", isCorrect: false },
        { answer: "해리", isCorrect: false },
        { answer: "뽀뽀, 삐삐", isCorrect: true },
      ],
    },
    {
      quizImg: { img06 },
      quizOptions: [
        { answer: "크롱", isCorrect: false },
        { answer: "루피", isCorrect: false },
        { answer: "로디", isCorrect: false },
        { answer: "해리", isCorrect: false },
        { answer: "에디", isCorrect: true },
      ],
    },
    {
      quizImg: { img07 },
      quizOptions: [
        { answer: "크롱", isCorrect: true },
        { answer: "루피", isCorrect: false },
        { answer: "로디", isCorrect: false },
        { answer: "해리", isCorrect: false },
        { answer: "뽀로로", isCorrect: false },
      ],
    },
    {
      quizImg: { img08 },
      quizOptions: [
        { answer: "크롱", isCorrect: false },
        { answer: "루피", isCorrect: false },
        { answer: "로디", isCorrect: false },
        { answer: "해리", isCorrect: false },
        { answer: "통통이", isCorrect: true },
      ],
    },
    {
      quizImg: { img09 },
      quizOptions: [
        { answer: "크롱", isCorrect: false },
        { answer: "루피", isCorrect: false },
        { answer: "로디", isCorrect: false },
        { answer: "해리", isCorrect: false },
        { answer: "패티", isCorrect: true },
      ],
    },
    {
      quizImg: { img10 },
      quizOptions: [
        { answer: "크롱", isCorrect: false },
        { answer: "루피", isCorrect: false },
        { answer: "로디", isCorrect: false },
        { answer: "해리", isCorrect: false },
        { answer: "포비", isCorrect: true },
      ],
    },
    {
      quizImg: { img11 },
      quizOptions: [
        { answer: "크롱", isCorrect: false },
        { answer: "루피", isCorrect: false },
        { answer: "로디", isCorrect: false },
        { answer: "해리", isCorrect: true },
        { answer: "뽀로로", isCorrect: false },
      ],
    },
  ];

  const [currentQuiz, setCurrentQuiz] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);

  const clickQuizOption = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }

    const nextQuiz = currentQuiz + 1;
    if (nextQuiz < quiz.length) {
      setCurrentQuiz(nextQuiz);
    } else {
      setShowScore(true);
    }
  };

  // 여기까지 추가

  return (
    <>
      {showScore ? (
        <div className="score-section">
          You scored {score} out of {questions.length}
        </div>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">
              {questions[currentQuestion].questionText}
            </div>
          </div>
          <div className="answer-section">
            {questions[currentQuestion].answerOptions.map((answerOption) => (
              <button
                onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}
              >
                {answerOption.answerText}
              </button>
            ))}
          </div>
        </>
      )}
    </>
  );
}

const PersonImg = styled.div`
  width: 100%;
  text-align: center;
`;

const PickBtn = styled.div`
  text-align: center;
  padding: 1%;
  margin-top: 10px;

  button {
    border: none;
    background-color: #ffdd6ec5;
    font-weight: 500;
    font-size: 13px;
    border-radius: 40px;
    margin-right: 5px;
    padding: 5px 17px 5px 17px;
  }
`;

