import React, { useState } from "react";
import styled from "styled-components";

export default function button(props) {
  return (
    <>
      <PersonImg>
        {/* 만약 다시하기를 클릭하면 숫자버튼은 초기화, 그리고 img01을 띄우고 그게 아니면 +1씩해서 띄우기 */}
      </PersonImg>
      <PickBtn>
        <button> {props.option}</button>
        <button>곰돌씨</button>
        <button>곰돌씨</button>
        <button>곰돌씨</button>
        <button>곰돌씨</button>
      </PickBtn>
    </>
  );
}

const PersonImg = styled.div`
  width: 100%;
  text-align: center;
`;

const PickBtn = styled.div`
  text-align: center;
  padding: 1%;
  margin-top: 10px;

  button {
    border: none;
    background-color: #ffdd6ec5;
    font-weight: 500;
    font-size: 13px;
    border-radius: 40px;
    margin-right: 5px;
    padding: 5px 17px 5px 17px;
  }
`;
